import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './Pages/Home';
import CreateAccount from './Pages/CreateAccount';
import CreateUsers from './Pages/CreateUsers';
import Login from './Pages/Login';
import Navbar from './Navbar';
import Topbar from './Topbar';
import './App.css';

function App() {
  // Example state to manage authentication status and user information
  const [auth, setAuth] = useState({
    isAuthenticated: false,
    user: null,
  });

  // Function to handle login
  const handleLogin = (user) => {
    setAuth({
      isAuthenticated: true,
      user: user,
    });
  };

  // Function to handle logout
  const handleLogout = () => {
    setAuth({
      isAuthenticated: false,
      user: null,
    });
  };

// Example: Make an API call to retrieve user information when the component mounts
useEffect(() => {
  // Check if the user is authenticated
  if (auth.isAuthenticated) {
    // Make an API call to get user information (replace with your actual API endpoint)
    fetch('./users')
      .then((response) => response.json())
      .then((data) => {
        // Update the user information in the state
        setAuth((prevAuth) => ({
          ...prevAuth,
          user: {
            ...data, // include other properties from the response
            name: data.username, // ensure the 'name' property exists in the user object
          },
        }));
      })
      .catch((error) => {
        console.error('Error fetching user information:', error);
      });
  }
}, [auth.isAuthenticated]);


  return (
    <body id="page-top">
      <div id="wrapper">
        <Navbar />
        <div id="content-wrapper" className="d-flex flex-column">
          <div id="content">
            <Router>
              {/* Include your top bar outside of the Routes */}
              <Topbar
                isAuthenticated={auth.isAuthenticated}
                user={auth.user}
                onLogout={handleLogout}
              />
              <Routes>
                {/* Pass the authentication state and login function to the Login component */}
                <Route
                  path="/Login"
                  element={<Login onLogin={handleLogin} />}
                />
                <Route path="/" element={<Home />} />
                <Route
                  path="/CreateAccount"
                  element={<CreateAccount />}
                />
                <Route
                  path="/CreateUsers"
                  element={<CreateUsers />}
                />
              </Routes>
            </Router>
          </div>
        </div>
      </div>
    </body>
  );
}

export default App;
