import React, { useState, useEffect } from 'react';
import ReactFlow, { ReactFlowProvider, addEdge, MiniMap, Controls, Background, useReactFlow } from 'react-flow-renderer';
import styled from 'styled-components';

// Initial elements for the flow chart
const initialElements = [
  { id: '1', data: { label: 'Team 1' }, position: { x: 0, y: 0 } },
  { id: '2', data: { label: 'Team 2' }, position: { x: 0, y: 100 } },
  { id: '3', data: { label: 'Team 3' }, position: { x: 0, y: 200 } },
  { id: '4', data: { label: 'Team 4' }, position: { x: 0, y: 300 } },
  { id: '5', data: { label: 'Winner 1-2' }, position: { x: 200, y: 50 } },
  { id: '6', data: { label: 'Winner 3-4' }, position: { x: 200, y: 250 } },
  { id: '7', data: { label: 'Champion' }, position: { x: 400, y: 150 } },
  { id: 'e1-5', source: '1', target: '5', animated: true },
  { id: 'e2-5', source: '2', target: '5', animated: true },
  { id: 'e3-6', source: '3', target: '6', animated: true },
  { id: 'e4-6', source: '4', target: '6', animated: true },
  { id: 'e5-7', source: '5', target: '7', animated: true },
  { id: 'e6-7', source: '6', target: '7', animated: true },
];

// Styled component for container
const Container = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  align-items: center;
  background: #f0f2f5;
`;

const ReactFlowWrapper = styled.div`
  width: 100%;
  height: 100%;
`;

const Brackets = () => {
  const [elements, setElements] = useState(initialElements);
  const { fitView } = useReactFlow();

  const onConnect = (params) => setElements((els) => addEdge(params, els));

  useEffect(() => {
    fitView({ padding: 0.1 });
  }, [fitView]);

  return (
    <ReactFlowProvider>
      <Container>
        <ReactFlowWrapper>
          <ReactFlow
            elements={elements}
            onConnect={onConnect}
            fitView
            defaultZoom={1.5}
            style={{ width: '100%', height: '100%' }}
          >
            <MiniMap />
            <Controls />
            <Background />
          </ReactFlow>
        </ReactFlowWrapper>
      </Container>
    </ReactFlowProvider>
  );
};

export default Brackets;
