import React, { useState, useEffect, useContext} from 'react';
import axios from 'axios';
import Searchbar from '../Searchbar';
import TableContent from '../TableContent';
import {AuthContext} from '../helpers/AuthContext';



function Home() {
  const [data, setData] = useState([]);
  const [search,setSearch] = useState('')
  const {authState, setAuthState} = useContext(AuthContext);

  useEffect(() => {
    // Check if accessToken exists in localStorage
    const accessToken = localStorage.getItem("accessToken");
  
    if (!accessToken) {
      // Handle the case where accessToken is not available
      console.error('Access token not found. API request not made.');
      return;
    }
  
    // Fetch data from the backend API
    axios.get('http://localhost:3001/tournaments', {
      headers: {
        accessToken: accessToken,
      },
      params: {
        tournament_name: search,
      },
    })
      .then(response => {
        if (response.data.error) {
          alert(response.data.error);
        } else {
          console.log(authState.id);
          setData(response.data);
        
        }
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, [search]);

  const onDelete = () => {
  axios.delete('http://localhost:3001/tournaments',data, {
      headers: {
        accessToken: accessToken,
      },
    })
      .then(response => {
        if (response.data.error) {
          alert(response.data.error);
        } else {
          console.log(authState.id);
        }
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
    }
  


  return (
    
    <div>
      <Searchbar
      search={search}
      setSearch={setSearch}
      />
      <TableContent
        items={data}
      />
      <div onClick={() => onDelete()}></div>
    </div>
  );
}

export default Home;
