import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import PropTypes from 'prop-types';

export const Divisions = ({ props, setProps }) => {
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const tournament_id = queryParams.get('tournament_id');
    const tournament_name = queryParams.get('tournament_name');
    const [data, setData] = useState([]);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleViewDetails = (divisionId, age_group, proficiency_level) => {
        const queryString = new URLSearchParams({ division_id: divisionId, age_group: age_group, proficiency_level: proficiency_level }).toString();
        navigate(`/Form?${queryString}`);
    };

    const fetchTournamentData = async () => {
        try {
            const response = await axios.get("http://localhost:3001/tournaments/default", {
                params: { tournament_id: tournament_id },
            });
            if (response.data.error) {
                setError(response.data.error);
            } else {
                setProps(response.data);
            }
        } catch (error) {
            console.error('Error fetching tournament data:', error);
            setError('An error occurred while fetching tournament data.');
        }
    };

    useEffect(() => {
        if (!props || !props.length) {
            fetchTournamentData();
        }
    }, [props, tournament_id]);

    useEffect(() => {
        const fetchDivisionsData = async () => {
            try {
                const response = await axios.get("http://localhost:3001/divisions/praticepent", {
                    params: { tournament_id: tournament_id },
                });
                if (response.data.error) {
                    setError(response.data.error);
                } else {
                    setData(response.data);
                }
            } catch (error) {
                console.error('Error fetching divisions data:', error);
                setError('An error occurred while fetching divisions data.');
            }
        };

        fetchDivisionsData();
    }, [tournament_id]);

    return (
        <div>
            <h1>{props.tournament_name}</h1>
            {error && <div className="alert alert-danger">{error}</div>}
            <table className="table">
                <thead>
                    <tr>
                        <th>Age Group</th>
                        <th>Proficiency Level</th>
                        <th>Register!</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((item, index) => (
                        <tr key={index}>
                            <td>{item.age_group}</td>
                            <td>{item.proficiency_level}</td>
                            <td>
                                <button
                                    className="btn btn-primary"
                                    onClick={() => handleViewDetails(item.division_id, item.age_group, item.proficiency_level)}
                                >
                                    Register!
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

Divisions.propTypes = {
    props: PropTypes.array,
    setProps: PropTypes.func.isRequired,
};
