import React from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import axios from "axios";

function CreatePost() {
  const initialValues = {
     account_type: "",
    account_name: "",
    account_description: "",
  };

  const validationSchema = Yup.object().shape({
     account_type: Yup.string().required("You must input a  account_type!"),
    account_name: Yup.string().required(),
    account_description: Yup.string().min(3).max(1000).required(),
  });

  const onSubmit = (data) => {
    axios.post("http://localhost:3001/users", data).then((response) => {
      console.log("IT WORKED");
    });
  };
  return (
    <div className="createPostPage">
      <Formik
        initialValues={initialValues}
        onSubmit={onSubmit}
        validationSchema={validationSchema}
      >
        <Form className="formContainer">
          <label> account_type: </label>
          <ErrorMessage name=" account_type" component="span" />
          <Field
            autocomplete="off"
            id="inputCreatePost"
            name=" account_type"
            placeholder="(Ex.  account_type...)"
          />
          <label>account_name: </label>
          <ErrorMessage name="account_name" component="span" />
          <Field
            autocomplete="off"
            id="inputCreatePost"
            name="account_name"
            placeholder="(Ex. account_name...)"
          />
          <label>account_description: </label>
          <ErrorMessage name="account_description" component="span" />
          <Field
            autocomplete="off"
            id="inputCreatePost"
            name="account_description"
            placeholder="(Ex. John123...)"
          />

          <button type="submit"> Create Post</button>
        </Form>
      </Formik>
    </div>
  );
}

export default CreatePost;
