import React, { useState, useEffect } from 'react';
import map from 'lodash/map';
import groupBy from 'lodash/groupBy';
import uniq from 'lodash/uniq';
import last from 'lodash/last';
import './Brackets.css';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const BracketApp = () => {
  const [brackets, setBrackets] = useState([]); // Store bracket elements
  const [fetchedBrackets, setFetchedBrackets] = useState([]); // Fetched bracket data with user1 and user2
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const division_id = queryParams.get('division_id') || '';

  // Fetch brackets from backend on component mount
  useEffect(() => {
    const fetchBrackets = async () => {
      if (!division_id) {
        console.error('No division_id provided.');
        return;
      }

      try {
        const response = await axios.get('http://localhost:3001/brackets', {
          params: { division_id },
        });

        setFetchedBrackets(response.data); // Store fetched brackets
        console.log('Fetched brackets:', response.data);
      } catch (error) {
        console.error('Error fetching brackets from backend:', error);
      }
    };

    fetchBrackets();
  }, [division_id]);

  // Generates brackets based on fetched data
  const generateBracket = () => {
    if (fetchedBrackets.length === 0) {
      console.error("No brackets found to display");
      return;
    }

    renderBrackets(fetchedBrackets); // Render fetched brackets
  };

  // Render the brackets with fetched data
  const renderBrackets = (struct) => {
    // Count how many rounds/groups we have
    const groupCount = uniq(map(struct, (s) => s.roundNo)).length;

    // Map through each group (round) and render the brackets for that round
    const allGroups = (
      <div className={`group${groupCount + 1}`} id={`b${brackets.length}`}>
        {map(Array.from({ length: groupCount }), (_, g) => (
          <div key={g + 1} className={`r${g + 1}`}>
            {map(groupBy(struct, (s) => s.roundNo)[g + 1], (gg, idx) => (
              <div key={idx}>
                <div className="bracketbox">
                  <span className="info1">{gg.bracketNo}</span>
                  <span className="info2">{gg.bracketNo}</span>
                  <span className="teama">{gg.user1}</span> {/* Display user1 */}
                  <span className="teamb">{gg.user2}</span> {/* Display user2 */}
                </div>
              </div>
            ))}
          </div>
        ))}
        <div className={`r${groupCount + 1}`}>
          <div className="final">
            <div className="bracketbox">
              <span className="teamc">{last(struct).user1}</span> {/* Display winner */}
            </div>
          </div>
        </div>
      </div>
    );

    // Set all groups in the brackets state to display them all at once
    setBrackets([allGroups]); // Replace the entire bracket list
  };

  const clearBrackets = () => {
    setBrackets([]); // Clear all displayed brackets
  };

  return (
    <div>
      <button id="add" onClick={generateBracket}>Show Brackets</button> {/* Generates brackets when clicked */}
      <button id="clear" onClick={clearBrackets}>Clear Brackets</button>
      <div className="brackets" id="brackets">{brackets}</div>
    </div>
  );
};

export default BracketApp;
