import React, { useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import './Brackets.css';

const BracketApp = () => {
  const [brackets, setBrackets] = useState([]);
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const division_id = queryParams.get('division_id') || '';

  // Fetch brackets when button is clicked
  const generateBracket = async () => {
    if (!division_id) {
      console.error('No division_id provided.');
      return;
    }

    try {
      const response = await axios.get('http://localhost:3001/brackets', {
        params: { division_id },
      });
      const fetchedBrackets = response.data;

      // Process and render brackets
      renderBrackets(fetchedBrackets);
    } catch (error) {
      console.error('Error fetching brackets:', error);
    }
  };

  const renderBrackets = (struct) => {
    const newBrackets = (
      <div className="bracket-container">
        {struct.map((bracket, idx) => (
          <div
            key={idx}
            className={`game ${bracket.winner ? 'winner-round' : 'round'}`} // Apply different layout for winners
          >
            <div className="game-top">
              <span className="teama">{bracket.user1}</span>
              <span>{bracket.score1}</span>
            </div>
            <div className="game-bottom">
              <span className="teamb">{bracket.user2}</span>
              <span>{bracket.score2}</span>
            </div>
          </div>
        ))}
      </div>
    );

    setBrackets(newBrackets);
  };

  return (
    <div>
      <button id="add" onClick={generateBracket}>Show Brackets</button>
      <button id="push">Push Brackets</button>
      <button id="clear" onClick={() => setBrackets([])}>Clear Brackets</button>
      <div className="brackets" id="brackets">
        {brackets}
      </div>
    </div>
  );
};

export default BracketApp;
