import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function Topbar({ isAuthenticated, user, onLogout }) {
  const [username, setUsername] = useState('');

  useEffect(() => {
    // Fetch user information and update the state when the component mounts
    if (isAuthenticated && user) {
      fetch('/api/user')  // Replace with your actual API endpoint
        .then((response) => response.json())
        .then((data) => {
          setUsername(data.name);
        })
        .catch((error) => {
          console.error('Error fetching user information:', error);
        });
    }
  }, [isAuthenticated, user]);

  return (
    <nav className="navbar navbar-expand navbar-light bg-white topbar mb-4 static-top shadow">
      {/* Sidebar Toggle (Topbar) */}
      <button id="sidebarToggleTop" className="btn btn-link d-md-none rounded-circle mr-3">
        <i className="fa fa-bars"></i>
      </button>

      {/* Topbar Navbar */}
      <ul className="navbar-nav ml-auto">
        {/* Nav Item - User Information */}
        {isAuthenticated ? (
          // If the user is authenticated, show user information and logout
          <li className="nav-item dropdown no-arrow">
            <a className="nav-link dropdown-toggle" href="#" id="userDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
              <span className="mr-2 d-none d-lg-inline text-gray-600 small">{username}</span>
              <img className="img-profile rounded-circle" src={user.profileImage} alt="User Profile" />
            </a>
            {/* Dropdown - User Information */}
            <div className="dropdown-menu dropdown-menu-right shadow animated--grow-in" aria-labelledby="userDropdown">
              <Link className="dropdown-item" to="/Profile">
                <i className="fas fa-user fa-sm fa-fw mr-2 text-gray-400"></i>
                Profile
              </Link>
              <Link className="dropdown-item" to="/Settings">
                <i className="fas fa-cogs fa-sm fa-fw mr-2 text-gray-400"></i>
                Settings
              </Link>
              <Link className="dropdown-item" to="/ActivityLog">
                <i className="fas fa-list fa-sm fa-fw mr-2 text-gray-400"></i>
                Activity Log
              </Link>
              <div className="dropdown-divider"></div>
              <a className="dropdown-item" href="#" onClick={onLogout}>
                <i className="fas fa-sign-out-alt fa-sm fa-fw mr-2 text-gray-400"></i>
                Logout
              </a>
            </div>
          </li>
        ) : (
          // If the user is not authenticated, show login and create account links
          <>
            <li className="nav-item dropdown no-arrow mx-1">
              <Link to="/Login" className="nav-link dropdown-toggle">
                Login
              </Link>
            </li>
            <li className="nav-item dropdown no-arrow">
              <Link to="/CreateAccount" className="nav-link dropdown-toggle">
                Create New Account
              </Link>
            </li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default Topbar;
