import React, { useState, useEffect } from 'react';
import find from 'lodash/find';
import map from 'lodash/map';
import filter from 'lodash/filter';
import uniq from 'lodash/uniq';
import groupBy from 'lodash/groupBy';
import random from 'lodash/random';
import last from 'lodash/last';
import './Brackets.css';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const BracketApp = () => {
  const [brackets, setBrackets] = useState([]);
  const [bracketCount, setBracketCount] = useState(0);
  const [fetchedBrackets, setFetchedBrackets] = useState([]);  // Fetched brackets with user1 and user2
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const division_id = queryParams.get('division_id') || '';

  const knownBrackets = [2, 4, 8, 16, 32, 64];

  useEffect(() => {
    console.log('division_id:', division_id); // Check if division_id is valid
    const fetchBrackets = async () => {
      if (!division_id) {
        console.error('No division_id provided.');
        return;
      }
  
      try {
        console.log('Fetching brackets for division_id:', division_id); // Debug log
        const response = await axios.get('http://localhost:3001/brackets', {
          params: { division_id },
        });
  
        setFetchedBrackets(response.data);  // Store fetched brackets
        console.log('Fetched brackets:', response.data);
      } catch (error) {
        console.error('Error fetching brackets from backend:', error);
      }
    };
  
    fetchBrackets();
  }, [division_id]);
  

  // Generates brackets based on fetched data
  const generateBracket = () => {
    let bracketSize = fetchedBrackets.length;  // Use fetched number of brackets
    if (bracketSize === 0) {
      console.error("No brackets found to display");
      return;
    }
  
    // Find the closest supported bracket size
    const closest = find(knownBrackets, (k) => k >= bracketSize);
    let byes = closest - bracketSize;
  
    if (byes > 0) bracketSize = closest;
  
    const newBrackets = [];
    let round = 1;
    let teamMark = 0;
  
    for (let i = 1; i <= bracketSize - 1; i++) {
      let isBye = false;
  
      // Handle byes
      if (byes > 0 && (i % 2 !== 0 || byes >= (bracketSize - i) / 2)) {
        isBye = true;
        byes--;
      }
  
      // Pair users directly from fetched brackets
      const user1 = !isBye && fetchedBrackets[teamMark] ? fetchedBrackets[teamMark].user1 : null;
      const user2 = !isBye && fetchedBrackets[teamMark] ? fetchedBrackets[teamMark].user2 : null;
  
      newBrackets.push({
        nextGame: i + 1 > bracketSize - 1 ? null : i + 1,
        user1: user1,
        user2: user2,
        bracketNo: i,
        roundNo: round,
        bye: isBye,
      });
  
      teamMark += 2; // Increment teamMark to move to the next pair
      if (i % 2 !== 0 && !isBye) teamMark--; // Adjust if odd-indexed and not a bye
    }
  
    console.log("newBrackets", newBrackets);
    renderBrackets(newBrackets);
  };
  
  const renderBrackets = (struct) => {
    const groupCount = uniq(map(struct, (s) => s.roundNo)).length;

    const group = (
      <div className={`group${groupCount + 1}`} id={`b${bracketCount}`}>
        {map(Array.from({ length: groupCount }), (_, g) => (
          <div key={g + 1} className={`r${g + 1}`}>
            {map(groupBy(struct, (s) => s.roundNo)[g + 1], (gg, idx) => (
              <div key={idx}>
                {gg.bye ? (
                  <div></div>
                ) : (
                  <div className="bracketbox">
                    <span className="info1">{gg.bracketNo}</span>
                    <span className="info2">{gg.bracketNo}</span>
                    <span className="teama">{gg.user1}</span>  {/* Display user1 */}
                    <span className="teamb">{gg.user2}</span>  {/* Display user2 */}
                  </div>
                )}
              </div>
            ))}
          </div>
        ))}
        <div className={`r${groupCount + 1}`}>
          <div className="final">
            <div className="bracketbox">
              <span className="teamc">{last(struct).user1}</span>  {/* Display winner */}
            </div>
          </div>
        </div>
      </div>
    );

    setBrackets((prevBrackets) => [...prevBrackets, group]);
    setBracketCount((prevCount) => prevCount + 1);
  };

  const clearBrackets = () => {
    setBrackets([]);
  };

  return (
    <div>
      <button id="add" onClick={generateBracket}>Show Brackets</button> {/* Generates brackets when clicked */}
      <button id="clear" onClick={clearBrackets}>Clear Brackets</button>
      <div className="brackets" id="brackets">{brackets}</div>
    </div>
  );
};

export default BracketApp;
