import React from 'react';
import axios from 'axios';

const ItemList = ({ items, accountId }) => {
  const accessToken = localStorage.getItem("accessToken");

  const onDelete = (tournamentName) => {
    if (!accessToken) {
      console.error('Access token not found. Delete request cannot be made.');
      return;
    }

    axios.delete(`http://localhost:3001/tournaments`, {
      headers: {
        accessToken: accessToken,
      },
      data: {
        tournament_name: tournamentName,
      }
    })
    .then(response => {
      console.log(response.data);
      console.log('Tournament deleted successfully.');
      // Optionally update your data state or perform other actions upon successful deletion
      window.location.reload(); // Reload the page after deletion (consider better UX alternatives)
    })
    .catch(error => {
      console.error('Error deleting tournament:', error);
    });
  };

  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Start Date</th>
          <th>End Date</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {items.map((item, index) => (
          <tr key={index}>
            <td>{item.tournament_name}</td>
            <td>{item.start_date}</td>
            <td>{item.end_date}</td>
            <td>
              {accountId === item.account_id ? (
                <div className="dropdown">
                  <button className="btn btn-primary dropdown-toggle" type="button" id={`dropdownMenuButton-${index}`} data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                    Actions
                  </button>
                  <div className="dropdown-menu" aria-labelledby={`dropdownMenuButton-${index}`}>
                    <button className="dropdown-item" onClick={() => onDelete(item.tournament_name)}>Delete</button>
                    {/* Add more dropdown actions (e.g., edit, view) here */}
                  </div>
                </div>
              ) : (
                <p>Not your Tournament</p>
              )}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default ItemList;
