import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './Pages/Home';
import CreateAccount from './Pages/CreateAccount';
import CreateUsers from './Pages/CreateUsers';
import Login from './Pages/Login';
import Navbar from './Navbar';
import Topbar from './Topbar';
import './App.css';

function App() {
  // Example state to manage authentication status and user information
  const [auth, setAuth] = useState({
    isAuthenticated: false,
    user: null,
  });

  // State to store the username
  const [usernameInApp, setUsernameInApp] = useState("");

  // Function to handle login
  const handleLogin = (user) => {
    setAuth({
      isAuthenticated: true,
      user: user,
    });
  };

  // Function to handle logout
  const handleLogout = () => {
    setAuth({
      isAuthenticated: false,
      user: null,
    });
  };

  // UseEffect to listen for changes in the usernameInApp state
  useEffect(() => {
    console.log("Username in App.js:", usernameInApp);
  }, [usernameInApp]);

  return (
    <body id="page-top">
      <div id="wrapper">
        <Navbar />
        <div id="content-wrapper" className="d-flex flex-column">
          <div id="content">
            <Router>
              {/* Include your top bar outside of the Routes */}
              <Topbar
                isAuthenticated={auth.isAuthenticated}
                user={auth.user}
                onLogout={handleLogout}
              />
              <Routes>
                {/* Pass the authentication state and login function to the Login component */}
                <Route
                  path="/Login"
                  element={<Login onLogin={handleLogin} setUsernameInApp={setUsernameInApp} />}
                />
                <Route path="/" element={<Home />} />
                <Route
                  path="/CreateAccount"
                  element={<CreateAccount />}
                />
                <Route
                  path="/CreateUsers"
                  element={<CreateUsers />}
                />
              </Routes>
            </Router>
          </div>
        </div>
      </div>
    </body>
  );
}

export default App;
