import React from 'react';
import { useNavigate } from "react-router-dom";

const ItemList = ({ items, accountId }) => {
  const navigate = useNavigate();

  const seeDivision = (tournamentName, tournamentId) => {
    const queryString = new URLSearchParams({ tournament_name: tournamentName, tournament_id: tournamentId }).toString();
    navigate(`/seeDivisions?${queryString}`);
  }

  return (
    <div className="d-flex flex-column justify-content-center align-items-center vh-100 text-primary">
      {items.map((item, index) => (
        <div key={index} style={{ backgroundImage: `url(${item.imageUrl})`, backgroundSize: 'cover', backgroundPosition: 'center', maxWidth: '600px', width: '80%' }} className="bg-black p-5 m-2 rounded">
          <h1 className="text-center">{item.tournament_name}</h1>
          <h5 className="text-center">{item.start_date} - {item.end_date}</h5>
          {accountId === item.account_id ? (
            <button className="btn btn-primary d-block mx-auto" onClick={() => seeDivision(item.tournament_name, item.tournament_id)}>Divisions</button>
          ) : (
            <h6 className="text-center">Not Your Tournament</h6>
          )}
          <div className="text-center">
            {item.imageUrl ? <img src={item.imageUrl} alt={item.tournament_name} width="100" /> : 'No Image'}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ItemList;
