import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

export const Divisions = ({ props, setProps }) => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const tournament_id = queryParams.get('tournament_id');
  const [data, setData] = useState([]);
  const [tournamentName, setTournamentName] = useState('');
  const navigate = useNavigate();

  const handleViewDetails = (item) => {
    setProps((key,prevArray) => prevArray[0][key]=item);
    const queryString = new URLSearchParams({ division_id: item.divisionId, age_group: item.age_group, proficiency_level: item.proficiency_level }).toString();
    navigate(`/Form?${queryString}`);
  };

  const fetchTournamentName = async () => {
    try {
      const response = await axios.get("http://localhost:3001/tournaments/default", {
        params: {
          tournament_id: tournament_id,
        },
      });
      if (response.data.error) {
        alert(response.data.error);
      } else {
        setTournamentName(response.data);
      }
    } catch (error) {
      console.error('Error fetching tournament name:', error);
    }
  };

  useEffect(() => {
    axios.get("http://localhost:3001/divisions/praticepent", {
      params: {
        tournament_id: tournament_id,
      },
    })
      .then(response => {
        if (response.data.error) {
          alert(response.data.error);
        } else {
          setData(response.data);
        }
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });

    // Fetch tournament name only if props is empty
    if (!props || props.length === 0) {
      fetchTournamentName();
    }
  }, [tournament_id, props]);

  return (
    <div>
      <h1>{tournamentName.tournament_name || (props.length !== 0 && props.tournament.tournament_name)}</h1>
      <table>
        <thead>
          <tr>
            <th>Age Group</th>
            <th>Proficiency Level</th>
            <th>Register!</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.age_group}</td>
              <td>{item.proficiency_level}</td>
              <td><button className="btn btn-primary" onClick={() => handleViewDetails(item)}> Register!</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
