import React from 'react';
import { useState,useEffect } from 'react';
import axios from 'axios';
import Searchbar from '../Searchbar';

const CompetitorView = () => {
    const [data, setData] = useState([]);
    const [search,setSearch] = useState('')

    useEffect(() => {
        // Check if accessToken exists in localStorage
        const accessToken = localStorage.getItem("accessToken");
        if (!accessToken) {
          // Handle the case where accessToken is not available
          console.error('Access token not found. API request not made.');
          return;
        }
      
        // Fetch data from the backend API
        axios.get('http://localhost:3001/tournaments', {
          headers: {
            accessToken: accessToken,
          },
          params: {
            tournament_name: search,
          },
        })
          .then(response => {
            if (response.data.error) {
              alert(response.data.error);
            } else {
              setData(response.data);
            
            }
          })
          .catch(error => {
            console.error('Error fetching data:', error);
          });
      }, [search]);
  
    return (
        <div className="d-flex flex-column justify-content-center align-items-center vh-100 text-primary">
             <Searchbar
      search={search}
      setSearch={setSearch}
      />
          {data.map((item, index) => (
        <div key={index} style={{ 
          backgroundImage: `url(${encodeURI(item.imageUrl)})`, // Encode the URL 
          backgroundSize: 'contain', 
          backgroundPosition: 'center',
          width: '40%',
          height: '300px', // Adjust height as needed
          margin: '10px',
          borderRadius: '10px',
          border: '2px solid black', // Add border for visualization
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          textAlign: 'center',
          color: '#000', // Set text color to contrast with background
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center'
        }}>
          <h1>{item.tournament_name}</h1>
          <h5>{item.start_date} - {item.end_date}</h5>
        </div>
      ))}
    </div>
  );
}
export default CompetitorView;
