import axios from 'axios';
import Dropdown from 'react-bootstrap/Dropdown';
import { useState } from 'react';
import CustomModal from './CustomModal';
import { useNavigate } from "react-router-dom";

const ItemList = ({ items, accountId }) => {
  const accessToken = localStorage.getItem("accessToken");
  const [showModal, setShowModal] = useState(false);
  const [openStates, setOpenStates] = useState(Array(items.length).fill(false));
  const navigate = useNavigate();

  const handleShowModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  const toggleDropdown = (index) => {
    const newOpenStates = [...openStates];
    newOpenStates[index] = !newOpenStates[index];
    setOpenStates(newOpenStates);
  };

  const seeDivision = (tournamentName, tournamentId) => {
    const queryString = new URLSearchParams({ tournament_name: tournamentName, tournament_id: tournamentId }).toString();
    navigate(`/seeDivisions?${queryString}`);
  };

  const onDelete = (tournament_id) => {
    if (!accessToken) {
      console.error('Access token not found. Delete request cannot be made.');
      return;
    }

    axios.delete(`http://localhost:3001/tournaments`, {
      headers: {
        accessToken: accessToken,
      },
      data: {
        tournament_id: tournament_id,
      }
    })
      .then(response => {
        console.log(response.data);
        console.log('Tournament deleted successfully.');
        window.location.reload();
      })
      .catch(error => {
        console.error('Error deleting tournament:', error);
      });
  };

  return (
    <div className="d-flex flex-column justify-content-center align-items-center vh-100 text-primary">
      {items.map((item, index) => (
        <div key={index} className="p-5 m-2 rounded text-white" style={{ 
          maxWidth: '600px', 
          width: '80%', 
          backgroundImage: `url(${item.imageUrl})`, 
          backgroundSize: 'cover', 
          backgroundPosition: 'center',
          color: 'white',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          textShadow: '0 1px 2px rgba(0, 0, 0, 0.5)' // Ensures text is readable on varied backgrounds
        }}>
          <h1 className="text-center">{item.tournament_name}</h1>
          <h5 className="text-center">{item.start_date} - {item.end_date}</h5>
          {accountId === item.account_id ? (
            <button className="btn btn-primary d-block mx-auto" onClick={() => seeDivision(item.tournament_name, item.tournament_id)}>Divisions</button>
          ) : (
            <h6 className="text-center">Not Your Tournament</h6>
          )}
          {accountId === item.account_id && (
            <Dropdown show={openStates[index]} onClick={() => toggleDropdown(index)}>
              <Dropdown.Toggle variant="primary" id={`dropdown-basic-${index}`} className="d-block mx-auto mt-2">
                Edit
              </Dropdown.Toggle>
              <Dropdown.Menu>
                <Dropdown.Item onClick={() => onDelete(item.tournament_id)}>Delete</Dropdown.Item>
                <Dropdown.Item onClick={handleShowModal}>Edit Tournament</Dropdown.Item>
                <CustomModal showModal={showModal} handleClose={handleCloseModal} accountId={accountId} tournament_id={item.tournament_id} />
              </Dropdown.Menu>
            </Dropdown>
          )}
        </div>
      ))}
    </div>
  );
};

export default ItemList;
