import axios from "axios";
import Identity from "../FormComponents/Identity";
import Password from "../FormComponents/Password";
import Credantials from "../FormComponents/Credantials";
import "../Form.css";
import { useState } from "react";

function CreateUsers(){

    const [pages, setPages] = useState(0);
    const [data, setData] = useState([]);
    const FormTitles = ["Identity", "Credntials", "Password"];

    const PageDisplay = () => {
        if (pages === 0) {
          return <Credantials data={data} setData={setData} />;
        } else if (pages === 1) {
          return <Password data={data} setData={setData} />;
        } else {
          return <Identity data={data} setData={setData} />;
        }
      };

    return (

        <div className="form">
        <div className="progressbar">
          <div
            style={{ width: pages === 0 ? "33.3%" : pages == 1 ? "66.6%" : "100%" }}
          ></div>
        </div>
        <div className="form-container">
          <div className="header">
          <h1>{FormTitles[pages]}</h1>
          </div>
          <div className="body">{PageDisplay()}</div>
          <div className="footer">
            <button
              disabled={pages == 0}
              onClick={() => {
                setPages((currPage) => currPage - 1);
              }}
            >
              Prev
            </button>
            <button
              onClick={() => {
                if (pages === FormTitles.length - 1) {
                  alert("FORM SUBMITTED");
                  console.log(setData);
                } else {
                  setPages((currPage) => currPage + 1);
                }
              }}
            >
              {pages === FormTitles.length - 1 ? "Submit" : "Next"}
            </button>
          </div>
        </div>
      </div>
   )
};
export default CreateUsers;
