import React from 'react'
import { useLocation } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import axios from "axios";
import { Container, Card, Button } from "react-bootstrap";
const ForgotPass = () => {
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const token = queryParams.get('token');

    const initialValues = {
        password: "", // Add password to initial values
      };
    
      const validationSchema = Yup.object().shape({
        password: Yup.string().password("Invalid password format").required("Password is required"), // Add password validation
      });
    
      const onSubmit = async (values, { setSubmitting }) => {
        console.log("Submitting values:", values);
        try {
            const response = await axios.post(
                "http://localhost:3001/users/verifypassword",
                values,
                {
                    headers: {
                       token:token,
                    }
                }
            );
            console.log("Request successful:", response.data);
            if (response.data.error) {
                alert(response.data.error);
            } else {
                alert(response.data.message);
            }
        } catch (error) {
            console.error("Error:", error.response ? error.response.data : error.message);
        } finally {
            setSubmitting(false);
        }
    };

    
    
      return (
        <Container style={{ marginTop: "20vh" }}>
          <Card bg="secondary" text="white">
            <Card.Body>
              <h4 className="card-title">Recover Password</h4>
              <p className="card-text">Please type in your password</p>
              <Formik
                initialValues={initialValues}
                onSubmit={onSubmit}
                validationSchema={validationSchema}
                enableReinitialize
              >
                {(formik) => (
                  <Form>
    
                    <div className="mb-3">
                      <label htmlFor="password" className="form-label">
                        Password:
                      </label>
                      <ErrorMessage name="password" component="div" className="text-danger" />
                      <Field
                        type="password"
                        id="password"
                        name="password"
                        placeholder="Enter password"
                        className="form-control"
                      />
                    </div>
    
                    <Button type="submit" variant="primary" disabled={formik.isSubmitting}>
                      Submit
                    </Button>
                  </Form>
                )}
              </Formik>
            </Card.Body>
          </Card>
        </Container>
      );
    };

export default ForgotPass
