import { Bracket, BracketGame } from 'react-tournament-bracket';

const matchData = {
  id: 1,
  name: 'Championship Game',
  scheduled: new Date().toISOString(),
  sides: {
    home: {
      team: {
        id: 1,
        name: 'Team A'
      },
      score: {
        score: 3
      }
    },
    visitor: {
      team: {
        id: 2,
        name: 'Team B'
      },
      score: {
        score: 2
      }
    }
  }
};

const rounds = [
  {
    title: 'Quarterfinals',
    games: [
      { id: 1, name: 'Game 1', sides: matchData.sides },
      { id: 2, name: 'Game 2', sides: matchData.sides },
    ]
  },
  {
    title: 'Semifinals',
    games: [
      { id: 3, name: 'Game 3', sides: matchData.sides }
    ]
  },
  {
    title: 'Finals',
    games: [
      { id: 4, name: 'Game 4', sides: matchData.sides }
    ]
  }
];

const Brackets = () => {
  return (
    <div style={{ maxWidth: '800px', margin: 'auto' }}>
      <Bracket
        rounds={rounds.map((round) => ({
          title: round.title,
          seeds: round.games.map((game) => ({
            id: game.id,
            date: game.scheduled,
            teams: [
              { id: game.sides.home.team.id, name: game.sides.home.team.name, score: game.sides.home.score.score },
              { id: game.sides.visitor.team.id, name: game.sides.visitor.team.name, score: game.sides.visitor.score.score },
            ]
          }))
        }))}
        renderSeedComponent={(props) => (
          <BracketGame {...props} styles={{ game: { backgroundColor: 'lightgrey' } }} />
        )}
      />
    </div>
  );
};

export default Brackets;
