import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode';

function Login({ onLogin, setUsernameInApp }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const navigate = useNavigate();

  const login = () => {
    const data = { username: username, password: password };
    axios.post("http://localhost:3001/users/Login", data).then((response) => {
      if (response.data.error) {
        alert(response.data.error);
      } else {
        // Assuming response.data contains user information
        const { accessToken, username } = response.data;

        // Decode the token to get additional information if needed
        const decodedToken = jwtDecode(accessToken);

        // Set the username in App.js using the setUsernameInApp callback
        setUsernameInApp(username);

        // Call the onLogin callback function passed from the App component
        onLogin({ accessToken, username, decodedToken });
        navigate("/");

        // Optionally, you can redirect the user or perform other actions
        // after successful login
      }
    });
  };

  return (
    <div className="loginContainer">
      <label>Username:</label>
      <input
        type="text"
        onChange={(event) => {
          setUsername(event.target.value);
        }}
      />
      <label>Password:</label>
      <input
        type="password"
        onChange={(event) => {
          setPassword(event.target.value);
        }}
      />

      <button onClick={login}> Login </button>
    </div>
  );
}

export default Login;
