// src/components/TournamentManager.js
import React, { useEffect, useState } from 'react';
import { BracketsManager } from 'brackets-manager';
import 'brackets-viewer';

const TournamentManager = () => {
  const [manager, setManager] = useState(null);

  useEffect(() => {
    const initializeTournament = async () => {
      const manager = new BracketsManager({
        // Add your database or storage implementation here
      });
      await manager.create({
        name: 'Sample Tournament',
        type: 'double_elimination',
        seeding: ['Team 1', 'Team 2', 'Team 3', 'Team 4'],
        settings: { seedOrdering: ['natural'] },
      });
      setManager(manager);
    };

    initializeTournament();
  }, []);

  useEffect(() => {
    if (manager) {
      window.bracketsViewer.render({
        manager,
        // Container element for brackets viewer
        containerId: '#brackets-container',
      });
    }
  }, [manager]);

  return <div id="brackets-container"></div>;
};

export default TournamentManager;
