import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from 'yup';
import axios from "axios";
import '../App.css';
import { useNavigate } from 'react-router-dom';
const CreateTournaments = () => {
    const navigate = useNavigate();

  const initialValues = {
    tournament_name: "",
    start_date: "",
    end_date: "",
  };

  const validationSchema = Yup.object().shape({
    tournament_name: Yup.string().required("You must create a Tournament Name!!"),
    start_date: Yup.date().nullable().required('Start Date is required'),
    end_date: Yup.date().nullable().required('End Date is required')
  });

  const onSubmit = (data, { setSubmitting }) => {
    console.log('Submitting data:', data);
  
    axios.post("http://localhost:3001/tournaments", data)
      .then((response) => {
        console.log("Request successful:", response.data);
        // Navigate to '/CreateUsers' after successful submission
        navigate('/CreateUsers');
      })
      .catch((error) => {
        console.error("Error:", error);
      })
      .finally(() => {
        // Ensure to reset the form submission state
        setSubmitting(false);
      });
  };
  return (
    <div className="createPostPage">
      <Formik
        initialValues={initialValues}
        onSubmit={onSubmit}
        validationSchema={validationSchema}
      >
        <Form className="formContainer">
          <div className="formField">
            <label htmlFor="tournament_name">Account Type:</label>
            <ErrorMessage name="tournament_name" component="span" />
            <Field
              type="text"
              id="tournament_name"
              name="tournament_name"
              placeholder="(Ex. tournament_name...)"
            />
          </div>

          <div className="formField">
            <label htmlFor="start_date">Account Name:</label>
            <ErrorMessage name="start_date" component="span" />
            <Field
              type="text"
              id="start_date"
              name="start_date"
              placeholder="(Ex. start_date...)"
            />
          </div>

          <div className="formField">
            <label htmlFor="end_date">Account Description:</label>
            <ErrorMessage name="end_date" component="span" />
            <Field
              type="text"
              id="end_date"
              name="end_date"
              placeholder="(Ex. John123...)"
            />
          </div>

          <button
          type="submit"
          >Create Tournament
          </button>
        </Form>
      </Formik>
    </div>
  );
}
export default CreateTournaments;
