import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

export const Divisions = ({ props, setProps }) => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const tournament_id = queryParams.get('tournament_id');
  const tournament_name = queryParams.get('tournament_name');
  const [error, setError] = useState('');
  const [data, setData] = useState([]);
  const navigate = useNavigate();

  const handleViewDetails = (divisionId, age_group, proficiency_level) => {
    const queryString = new URLSearchParams({ division_id: divisionId, age_group, proficiency_level }).toString();
    navigate(`/Form?${queryString}`);
  };

  const apiCall = async () => {
    try {
      const response = await axios.get('http://localhost:3001/tournaments/default', {
        params: {
          tournament_id: tournament_id,
        },
      });
      if (response.data.error) {
        setError(response.data.error);
      } else {
        setProps(response.data);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('An error occurred while fetching tournament data.');
    }
  };

  useEffect(() => {
    if (!props || !props.length) {
      apiCall();
    }
  }, [props, tournament_id, setProps]);

  useEffect(() => {
    axios.get('http://localhost:3001/divisions/praticepent', {
      params: {
        tournament_id: tournament_id,
      },
    })
      .then(response => {
        if (response.data.error) {
          alert(response.data.error);
        } else {
          setData(response.data);
        }
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, [tournament_id]);

  return (
    <div>
      <h1>{props?.tournament_name || tournament_name}</h1>
      <table>
        <thead>
          <tr>
            <th>Age Group</th>
            <th>Proficiency Level</th>
            <th>Register!</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.age_group}</td>
              <td>{item.proficiency_level}</td>
              <td>
                <button className="btn btn-primary" onClick={() => handleViewDetails(item.division_id, item.age_group, item.proficiency_level)}>
                  Register!
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Divisions;
