import axios from 'axios';
import Dropdown from 'react-bootstrap/Dropdown';
import { useState } from 'react';
import CustomModal from './CustomModal';
const ItemList = ({ items,accountId }) => {
    const accessToken = localStorage.getItem("accessToken");
    const [showModal, setShowModal] = useState(false);

    const handleShowModal = () => setShowModal(true);
    const handleCloseModal = () => setShowModal(false)

    const [isOpen, setIsOpen] = useState(false);

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    };
    const onDelete = (tournamentName) => {
        if (!accessToken) {
          console.error('Access token not found. Delete request cannot be made.');
          return;
        }

        axios.delete(`http://localhost:3001/tournaments`, {
          headers: {
            accessToken: accessToken,
          },
          data: {
            tournament_name: tournamentName,
          }
        })
          .then(response => {
            console.log(response.data);
            console.log('Tournament deleted successfully.');
            window.location.reload();
            // Optionally update your data state or perform any other actions upon successful deletion
          })
          .catch(error => {
            console.error('Error deleting tournament:', error);
          });
      };

      return (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Start Date</th>
              <th>End Date</th>
              <th>Edit</th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, index) => (
              <tr key={index}>
                <td>{item.tournament_name}</td>
                <td>{item.start_date}</td>
                <td>{item.end_date}</td>
                <td>
                    {accountId == item.account_id ? (
                      <Dropdown show={isOpen} onClick={toggleDropdown}>
                      <Dropdown.Toggle variant="primary" id="dropdown-basic">
                        Edit
                      </Dropdown.Toggle>
                      <Dropdown.Menu>
                  <Dropdown.Item onClick={() => onDelete(item.tournament_name)}>Delete</Dropdown.Item>
                  <Dropdown.Item onClick={handleShowModal}> Edit Tournament</Dropdown.Item>
                  <CustomModal showModal={showModal} handleClose={handleCloseModal} />
                  </Dropdown.Menu>
            </Dropdown>
                    ):(<h6> Not your Tournament</h6>)}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      );
    };

    export default ItemList;
