import Home from './Pages/Home';
import CreateAccount from './Pages/CreateAccount';
import CreateUsers from './Pages/CreateUsers';
import Login from './Pages/Login';
import Navbar from './Navbar';
import LandingPage from './Pages/LandingPage';
import Tolpbar from './Tolpbar';
import {AuthContext} from './helpers/AuthContext';
import { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import {BrowserRouter as Router,Route,Routes} from 'react-router-dom';



function App() {
const [authState, setAuthState] = useState({username:"", id:0, status:false});

useEffect(() => {
  console.log("firing api");
  const accessToken = localStorage.getItem("accessToken");
  if (!accessToken) {
    // Handle the case where there is no access token (e.g., redirect to login page)
    setAuthState({...authState, status:false});
  } else{
  axios.get("http://localhost:3001/users/auth", {
    headers: {
      accessToken: accessToken,
    },
  })
    .then((response) => {
      console.log("got a response", response);
      if (response.data.error) {
        setAuthState({...authState, status:false});
      } else {
        console.log(response.data.username);
        setAuthState({username:response.data.username, id:response.data.id, status:true});
      }
    });
  }
},[]); 


return (
  <div>
    <AuthContext.Provider value={{ authState, setAuthState }}>
      {authState.status && <Navbar />}
      <Router>
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-3 col-xl-2 px-0">
              {/* Tolpbar will go here */}
              <Tolpbar />
            </div>
            <div className="col-md-9 col-xl-10">
              <Routes>
                <Route path="/Login" element={<Login />} />
                <Route path="/Home" element={<Home />} />
                <Route path="/CreateAccount" element={<CreateAccount />} />
                <Route path="/CreateUsers" element={<CreateUsers />} />
                <Route path="/" element={<LandingPage />} />
              </Routes>
            </div>
          </div>
        </div>
      </Router>
    </AuthContext.Provider>
  </div>
);
}

export default App;
