import React from 'react';
import { Link } from 'react-router-dom';

function Topbar({ isAuthenticated, user, onLogout }) {
  return (
    <nav className="navbar navbar-expand navbar-light bg-white topbar mb-4 static-top shadow">
      {/* Sidebar Toggle (Topbar) */}
      <button id="sidebarToggleTop" className="btn btn-link d-md-none rounded-circle mr-3">
        {/* You can add an icon here if needed */}
        {/* <i className="fa fa-bars"></i> */}
      </button>

      {/* Topbar Navbar */}
      <ul className="navbar-nav ml-auto">
        {/* Nav Item - User Information */}
        {isAuthenticated && user && (
          // If the user is authenticated, show username and logout
          <li className="nav-item">
            <span className="nav-link">
              {user.name ? `Welcome, ${user.name}` : 'User Not Found'}
            </span>
          </li>
        )}
        {!isAuthenticated && (
          // If the user is not authenticated, show login and create account links
          <>
            <li className="nav-item dropdown no-arrow mx-1">
              <Link to="/Login" className="nav-link dropdown-toggle">
                Login
              </Link>
            </li>
            <li className="nav-item dropdown no-arrow">
              <Link to="/CreateAccount" className="nav-link dropdown-toggle">
                Create New Account
              </Link>
            </li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default Topbar;
