import React from 'react';
import { useState,useEffect } from 'react';
import axios from 'axios';
import Searchbar from '../Searchbar';

const CompetitorView = () => {
    const cars = ["Saab", "Volvo", "BMW", "dad"];
    const [data, setData] = useState([]);
    const [search,setSearch] = useState('')

    useEffect(() => {
        // Check if accessToken exists in localStorage
        const accessToken = localStorage.getItem("accessToken");
        if (!accessToken) {
          // Handle the case where accessToken is not available
          console.error('Access token not found. API request not made.');
          return;
        }
      
        // Fetch data from the backend API
        axios.get('http://localhost:3001/tournaments', {
          headers: {
            accessToken: accessToken,
          },
          params: {
            tournament_name: search,
          },
        })
          .then(response => {
            if (response.data.error) {
              alert(response.data.error);
            } else {
              console.log(authState.id);
              setData(response.data);
            
            }
          })
          .catch(error => {
            console.error('Error fetching data:', error);
          });
      }, [search]);
  
    return (
        <div className="d-flex flex-column justify-content-center align-items-center vh-100 text-primary">
             <Searchbar
      search={search}
      setSearch={setSearch}
      />
            {cars.map((car, index) => (
                <div key={index} className="bg-black p-5 m-2 rounded" style={{ maxWidth: '600px', width: '80%' }}>
                    <h1 className="text-center">{car}</h1>
                    <h5 className="text-center">Start Date End Date</h5>
                    <button className="btn btn-primary d-block mx-auto">Sign Up!</button>
                </div>
            ))}
        </div>
    );
};

export default CompetitorView;
