import React from 'react';
import { Link } from 'react-router-dom';

function Topbar({ isAuthenticated, user, onLogout }) {
  return (
    <nav className="navbar navbar-expand navbar-light bg-white topbar mb-4 static-top shadow">
      {/* Sidebar Toggle (Topbar) */}
      <button id="sidebarToggleTop" className="btn btn-link d-md-none rounded-circle mr-3">
        <i className="fa fa-bars"></i>
      </button>

      {/* Topbar Navbar */}
      <ul className="navbar-nav ml-auto">
        {/* Nav Item - User Information */}
        {isAuthenticated ? (
          // If the user is authenticated, show user name and logout
          <li className="nav-item">
            <span className="nav-link">
              <span className="mr-2 d-none d-lg-inline text-gray-600 small">{user.name}</span>
            </span>
            <a href="#" className="nav-link" onClick={onLogout}>
              Logout
            </a>
          </li>
        ) : (
          // If the user is not authenticated, show login and create account links
          <>
            <li className="nav-item">
              <Link to="/Login" className="nav-link">
                Login
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/CreateAccount" className="nav-link">
                Create New Account
              </Link>
            </li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default Topbar;
