import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import Tolpbar from './Tolpbar';
import Leftbar from './Leftbar';
import Home from './Pages/Home';
import CreateUsers from './Pages/CreateUsers';
import Login from './Pages/Login';
import LandingPage from './Pages/LandingPage';
import CreateTournaments from './Pages/CreateTournaments';
import MyTournaments from './Pages/MyTournaments';
import AccountUser from './Pages/AccountUser';
import CompetitorView from './Pages/CompetitorView';
import { AuthContext } from './helpers/AuthContext';

function App() {
  const [authState, setAuthState] = useState({
    username: "",
    id: 0,
    status: false,
    account_id: 0
  });

  useEffect(() => {
    const fetchAuthState = async () => {
      const accessToken = localStorage.getItem("accessToken");
      if (accessToken) {
        try {
          const response = await axios.get("http://localhost:3001/users/auth", {
            headers: {
              accessToken: accessToken,
            },
          });
          const userData = response.data;
          setAuthState({
            username: userData.username,
            id: userData.id,
            status: true,
            account_id: userData.account_id
          });
        } catch (error) {
          console.error("Error fetching authentication:", error);
          setAuthState({ ...authState, status: false });
        }
      } else {
        setAuthState({ ...authState, status: false });
      }
    };

    fetchAuthState(); // Call the async function within useEffect

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // Empty dependency array to run only once on component mount

  return (
    <div className="container-fluid">
      <Router>
        <AuthContext.Provider value={{ authState, setAuthState }}>
          <div className='row flex-nowrap'>
            <Tolpbar />
          </div>
          <div className='row flex-nowrap'>
            {authState.status && (
              <div className="col-auto col-md-3 col-xl-2 px-sm-2 px-0 bg-dark">
                <Leftbar />
              </div>
            )}
            <div className='col'>
              <Routes>
                <Route path='/Login' element={<Login />} />
                <Route path='/CreateUsers' element={<CreateUsers />} />
                <Route path='/' element={!authState.status ? <Navigate to="/LandingPage" /> : <Navigate to="/Home" />} />
                <Route path='/AccountUser' element={<AccountUser />} />
                <Route path='/CompetitorView' element={<CompetitorView />} />
                <Route path='/LandingPage' element={<LandingPage />} />
                <Route path='/CreateTournaments' element={<CreateTournaments />} />
                <Route path='/MyTournaments' element={<MyTournaments />} />
                <Route path='/Home' element={<Home />} />
              </Routes>
            </div>
          </div>
        </AuthContext.Provider>
      </Router>
    </div>
  );
}

export default App;
