import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation, useHistory } from 'react-router-dom';

export const Divisions = () => {
  const location = useLocation();
  const history = useHistory();
  const queryParams = new URLSearchParams(location.search);
  const tournamentId = queryParams.get('tournament_id');
  const [data, setData] = useState([]);
  const [tournamentName, setTournamentName] = useState('');

  const handleViewDetails = (item) => {
    history.push(`/Form?division_id=${item.divisionId}&age_group=${item.age_group}&proficiency_level=${item.proficiency_level}`);
  };

  const fetchTournamentDivisions = async () => {
    try {
      const response = await axios.get("http://localhost:3001/divisions/practicepent", {
        params: { tournament_id: tournamentId }
      });
      setData(response.data);
    } catch (error) {
      console.error('Error fetching divisions:', error);
    }
  };

  const fetchTournamentName = async () => {
    try {
      const response = await axios.get("http://localhost:3001/tournaments/default", {
        params: { tournament_id: tournamentId }
      });
      setTournamentName(response.data.tournament_name);
    } catch (error) {
      console.error('Error fetching tournament name:', error);
    }
  };

  useEffect(() => {
    fetchTournamentDivisions();
    fetchTournamentName();
  }, [tournamentId]);

  useEffect(() => {
    const handleLocationChange = () => {
      if (location.pathname === "/Divisions") {
        fetchTournamentDivisions();
      }
    };

    const unlisten = history.listen(handleLocationChange);

    return () => {
      unlisten();
    };
  }, [history, location.pathname]);

  return (
    <div>
      <h1>{tournamentName}</h1>
      <table>
        <thead>
          <tr>
            <th>Age Group</th>
            <th>Proficiency Level</th>
            <th>Register!</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.age_group}</td>
              <td>{item.proficiency_level}</td>
              <td><button className="btn btn-primary" onClick={() => handleViewDetails(item)}> Register!</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
