import React from 'react';
import { useNavigate } from "react-router-dom";

const ItemList = ({ items, accountId }) => {
  const navigate = useNavigate();

  const seeDivision = (tournamentName, tournamentId) => {
    const queryString = new URLSearchParams({ tournament_name: tournamentName, tournament_id: tournamentId }).toString();
    navigate(`/seeDivisions?${queryString}`);
  }

  return (
    <div className="d-flex flex-column justify-content-center align-items-center vh-100 text-primary">
      {items.map((item, index) => (
        <div key={index} style={{ 
          backgroundImage: `url(${item.imageUrl})`, 
          backgroundSize: 'cover', 
          backgroundPosition: 'center',
          width: '100%',
          height: '300px', // Adjust height as needed
          margin: '10px',
          borderRadius: '10px',
          border: '2px solid black', // Add border for visualization
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          textAlign: 'center',
          color: '#000', // Set text color to contrast with background
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center'
        }}>
          <h1>{item.tournament_name}</h1>
          <h5>{item.start_date} - {item.end_date}</h5>
          {accountId === item.account_id ? (
            <button className="btn btn-primary" onClick={() => seeDivision(item.tournament_name, item.tournament_id)}>Divisions</button>
          ) : (
            <h6>Not Your Tournament</h6>
          )}
        </div>
      ))}
    </div>
  );
};

export default ItemList;
