// Importing necessary dependencies
import React from 'react';
import { Bracket as ReactBracket } from 'react-bracket';
import styled from 'styled-components';

// Styling the wrapper div using styled-components
const Wrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
`;

// Data for the tournament bracket
const matchData = {
  name: 'Quarterfinals',
  seeds: [
    { id: 1, date: new Date().toDateString(), teams: [{ name: 'Team A' }, { name: 'Team B' }], },
    { id: 2, date: new Date().toDateString(), teams: [{ name: 'Team C' }, { name: 'Team D' }], },
    { id: 3, date: new Date().toDateString(), teams: [{ name: 'Team E' }, { name: 'Team F' }], },
    { id: 4, date: new Date().toDateString(), teams: [{ name: 'Team G' }, { name: 'Team H' }], },
  ],
};

// React component for the tournament bracket
const Brackets = () => {
  // Defining rounds for the bracket
  const rounds = [
    { title: 'Quarterfinals', seeds: matchData.seeds },
    { title: 'Semifinals', seeds: matchData.seeds.slice(0, 2) },
    { title: 'Finals', seeds: matchData.seeds.slice(0, 1) },
  ];

  return (
    <Wrapper>
      <ReactBracket rounds={rounds} /> // Rendering the Bracket component with provided rounds
    </Wrapper>
  );
};

export default Brackets; // Exporting the TournamentBracket component
